import { Component, OnInit } from '@angular/core';
import axios from 'axios';


@Component({
  selector: 'app-new',
  templateUrl: './new.component.html',
  styleUrls: ['./new.component.css']
})


export class NewComponent implements OnInit{
  product:any[]=[];


  constructor(){}
  ngOnInit(): void {
    axios.get("http://localhost:5000/api/Product/")
    .then(res=>{
      console.log(res.data[0])
      this.product = res.data;
    }).catch(err=>{
      console.log(err);
    })
  }

  name: any[]=[];
  price: any[]=[];
  disc: any[]=[];

  async createPost() {
    const response = await axios.post("http://localhost:5000/api/Product/", {
      name: this.name,
      price: this.price,
      disc: this.disc
    } )
    this.ngOnInit()
  }

  async deleteProduct (id:String){
    await axios.delete(`http://localhost:5000/api/Product/${id}`) .then(()=>{
      this.ngOnInit()
    })
  } 
}




<form class="form" (ngSubmit)="createPost()">
  <label>
    Name :
  </label>  <input type="text" [(ngModel)]="name" name="name" />
  <br />
  <label >
    Price :
  </label>  <input type="number" [(ngModel)]="price" name="price" />
  <br />
  <label >
    Discription :
  </label> <input type="text" [(ngModel)]="disc" name="disc" />
  <br />
  <button class="btn" type="submit">Create Post</button>
</form>






<div class="card">
    <div *ngFor="let item of product">
      <div class="div">{{item.name}}</div>  
      <div class="div2">Price : {{item.price}}</div> 
      <div class="div3">{{item.disc}}</div> 
      <button (click)="deleteProduct(item._id)">Delete</button>
      item    </div>

</div>
