import { Component, OnInit } from '@angular/core';
import axios from 'axios';
import * as e from 'cors';

@Component({
  selector: 'app-new',
  templateUrl: './new.component.html',
  styleUrls: ['./new.component.css']
})


export class NewComponent implements OnInit{
  product:any[]=[];
  name :any[]=[];


  constructor(){}
  ngOnInit(): void {
    axios.get("http://localhost:5000/api/Product/")
    .then(res=>{
      console.log(res.data[0])
      this.product = res.data;
    }).catch(err=>{
      console.log(err);
    })
  }

  inputChanged(event:any) {
   event.target.value

  }

}


<form class="form">
    <label for="">Name</label>
    <div><input type="text" placeholder="Name" value="name" (change)="inputChanged($event)"></div>
    <label for="">Price</label>
    <div><input type="number" placeholder="Price" value="price" (change)="inputChanged($event)"></div>
    <label for="">Discription</label>
   <div><input type="text" placeholder="Discription" value="disc" (change)="inputChanged($event)"></div> 
   <button class="button" onclick="">Add</button>
</form>
<form (ngSubmit)="createPost()">
  <label>
    Name :
    <input type="text" [(ngModel)]="name" name="name" />
  </label>
  <br />
  <label>
    Price
    <textarea [(ngModel)]="price" name="content"></textarea>
  </label>
  <br />
  <button type="submit">Create Post</button>
</form>
<div class="card">
    <div *ngFor="let item of product">
      <div class="div">{{item.name}}</div>  
      <div class="div2">Price : {{item.price}}</div> 
      <div class="div3">{{item.disc}}</div> 
    </div>
</div>
