import { Component, OnInit } from '@angular/core';
import axios from 'axios';


@Component({
  selector: 'app-new',
  templateUrl: './new.component.html',
  styleUrls: ['./new.component.css']
})


export class NewComponent implements OnInit{
  product:any[]=[];


  constructor(){}
  ngOnInit(): void {
    axios.get("http://localhost:5000/api/Product/")
    .then(res=>{
      console.log(res.data[0])
      this.product = res.data;
    }).catch(err=>{
      console.log(err);
    })
  }

  name: any[]=[];
  price: any[]=[];
  disc: string[]=[];

  async createPost() {
    const response = await axios.post("http://localhost:5000/api/Product/", {
      title: this.name,
      price: this.price,
      disc: this.disc,
    });
    console.log(response.data);
  }
}





<form (ngSubmit)="createPost()">
  <label>
    Name :
    <input type="text" [(ngModel)]="name" name="name" />
  </label>
  <br />
  <label>
    Price :
    <input type="number" [(ngModel)]="price" name="price" />
  </label>
  <label>
    Discription :
    <input type="text" [(ngModel)]="disc" name="disc" />
  </label>
  <br />
  <button type="submit">Create Post</button>
</form>






<div class="card">
    <div *ngFor="let item of product">
      <div class="div">{{item.name}}</div>  
      <div class="div2">Price : {{item.price}}</div> 
      <div class="div3">{{item.disc}}</div> 
    </div>
</div>
