import { Component, OnInit } from '@angular/core';
import axios from 'axios';

@Component({
  selector: 'app-new',
  templateUrl: './new.component.html',
  styleUrls: ['./new.component.css']
})


export class NewComponent implements OnInit{
  product:any[]=[];

  constructor(){}
  ngOnInit(): void {
    axios.get("http://localhost:5000/api/Product/")
    .then(res=>{
      console.log(res.data[0])
      this.product = res.data;
    }).catch(err=>{
      console.log(err);
    })
  }

  inputChanged(event:any) {

  }

}


<form class="form">
    <label for="">Name</label>
    <div><input type="text" placeholder="Name" value="name" (change)="inputChanged($event)"></div>
    <label for="">Price</label>
    <div><input type="number" placeholder="Price" value="" (change)="inputChanged($event)"></div>
    <label for="">Discription</label>
   <div><input type="text" placeholder="Discription" (change)="inputChanged($event)"></div> 
   <button class="button" onclick="">Add</button>
</form>
<div class="card">
    <div *ngFor="let item of product">
      <div class="div">{{item.name}}</div>  
      <div class="div2">Price : {{item.price}}</div> 
      <div class="div3">{{item.disc}}</div> 
    </div>
</div>
